import React, { Component, useRef, useState } from 'react';
import { Container, Button, Form, Row, Col, Card } from 'react-bootstrap';
import axios from 'axios';

function FileUpload() {

    const fileRef = useRef(null);
    const [File, setFile] = useState();
    const [Order, setOrder] = useState({});
    // const [Selected, setSelected] = useState(false);

    // const changeHandler = (event) => {
    //     setFile(event.target.files[0]);
    //     setSelected(true);
    // }

    const handleSubmit = () => {

        const formData = new FormData();
        formData.append('file', fileRef.current.files[0]);

        axios.post('/api/upload', formData, {
            headers: {'Content-Type':'multipart/form-data'}
        }).then(res => {
            console.log(res);
            setOrder(res.data.orderData.order);
            //console.log(res.data)
        });

    }

    const toppingsArea = (area) => {
        if(area == "0")
            return "Whole";
        else if(area == "1")
            return "First-Half";
        else if(area == "2")
            return "Second-Half";
    }

    return (
        <Container>
        <Row>
            <Col xs={6} md={4}>
                <Form.Group controlId="formFile" className="mb-3" style={{paddingTop: "1rem"}}>
                    <Form.Label>Default file input example</Form.Label>
                    <Form.Control type="file" ref={fileRef}/>
                </Form.Group>
                <div>
                    <Button variant="primary" onClick={() => handleSubmit()}>Submit</Button>
                </div>
            </Col>
            <Col xs={12} md={8}>
                <Container>
                    <Card style={{marginTop: "1rem"}}>
                    <Card.Header>Featured</Card.Header>
                        { Order.id ? (
                                <Card.Body style={{maxHeight:"400px", overflowY: "scroll"}}>
                                    <div>Order {Order.id.number}:</div>
                                    { Order.pizza.map((pizza, pizzaIndex) => {
                                        return (
                                        <div key={pizzaIndex} style={{textIndent:'50px'}}>
                                            <div key={pizza.id.number}> Pizza {pizza.id.number} - {pizza.size[0]}, {pizza.crust[0]}, {pizza.type[0]}</div> 
                                            { pizza.toppings ? (
                                                pizza.toppings.map((toppings, toppingIndex) => {
                                                    return <div key={toppingIndex} style={{textIndent:'100px'}}>Toppings {toppingsArea(toppings.id.area)} {
                                                        toppings.item.map((i, index) => {
                                                            return <div key={index} style={{textIndent:'150px'}}>{i}</div>
                                                        })
                                                    } </div>
                                                })
                                            ) : (<></>)
                                            } 
                                        </div>);
                                    }) }
                                </Card.Body>
                        ) : (
                            <div></div>
                        )}
                    </Card>
                </Container>
            </Col>
        </Row>          
        </Container>
    )
}

export default FileUpload;
