import React, {useEffect, useState} from 'react';
import axios from 'axios';

import KairosPagination from './kairosPagination';

function ViewOrders () {

    const [order, setOrder] = useState([]);
    const [curPage, setCurPage] = useState(1);
    const [totalPage, setTotalPage] = useState(20);

    const getOrder = async (pageNumber) => {
        let uri = `/api/orders`;
        if(pageNumber){
            uri = `/api/orders?limit=10&page=${pageNumber}`;
        }
        axios.get(uri).then(res => {
            setOrder(res.data);
        });
    }

    const getTotalOrder = async () => {
        let uri = `/api/orders/total`;
        await axios.get(uri).then(res => {
            console.log(res)
            //setTotalPage(res);
        });
    } 

    const pageClickHandler = (pageNumber) => {
        setCurPage(pageNumber);
        getOrder(pageNumber);
    };

    useEffect(() => {
        getTotalOrder();
        getOrder(curPage);
    }, []);

    return (
        <KairosPagination
            totPages={totalPage}
            currentPage={curPage}
            pageClicked={(ele) => {
                pageClickHandler(ele);
            }}>
            {order.length > 0 ? (
                <ul>
                    {order.map(o => {
                        return <li > ORDER NO. {o.orderData.order.id.number} </li>
                    })}
                </ul>
            ) : (<></>)}
        </KairosPagination>
    );
}

export default ViewOrders;
