    render() {
        return (
            <div sample={totalPage}>
        <KairosPagination
            totPages={totalPage}
            currentPage={curPage}
            pageClicked={(ele) => {
                pageClickHandler(ele);
            }}>
            <Container>
                <InputGroup className="mb-3" style={{paddingTop: "1rem"}}>
                    <InputGroup.Text id="basic-addon1">Search Here</InputGroup.Text>
                    <FormControl
                        aria-label="Username"
                        aria-describedby="basic-addon1"
                    />
                </InputGroup>
                <Table striped bordered hover>
                    <thead>
                        <tr>
                            <th>#</th>
                            <th>Order Number</th>
                            <th>Order Date</th>
                            <th>Order Data</th>
                        </tr>
                    </thead>
                    {order.length > 0 ? (
                        <tbody>
                            {order.map((o, i) => {
                                return (<tr key={(i+1)}>
                                    <td > {(i+1)} </td>
                                    <td > {o.orderData.order.id.number} </td>
                                    <td > {o.orderDate} </td>
                                    
                                </tr>)
                            })}
                        </tbody>
                    ) : (
                        <tbody>    
                            <tr>
                                <td colSpan="4">No Order Found</td>
                            </tr>
                        </tbody>)
                    }
                </Table>
            </Container>
        </KairosPagination>
        </div>
        );
    }
}

export default ViewOrders;
