import React, { Component, useState, useEffect, useReducer } from 'react';
import FileUpload from './fileUpload';
import { BrowserRouter as Router, Route, Switch } from 'react-router-dom';
import Footer from './footer';
import Navi from './navi';
import ViewOrders from './viewOrders';
import ViewToppings from './viewToppings';
import OrderContext from './OrderContext';
import InputOrders from './inputOrders';
import ModalError from '../modals/modalError';

function Home () {

    const modalInfo = {
        modalShow: false,
        modalTitle: 'Error Message',
        modalDesc: 'Invalid data'
    }

    const modalReducer = (modalState, funcData) => {
        //console.log({...modalState, modalShow:funcData});
        setTimeout(() => {
            console.log(1111);
        }, 1000);
        return {...modalState, modalShow:funcData};
    }

    const [ Order, setOrder ] = useState({});
    const [ modalState, modalDispatch ] = useReducer(modalReducer, modalInfo);

    return (
        <>
        <OrderContext.Provider value={{Order, setOrder, modalState, modalDispatch}} >
            <ModalError show={modalState.modalShow} onHide={() => modalDispatch(false)} info={modalState}></ModalError>
            <Router>
                <Navi></Navi>
                <Switch>
                    <Route path="/uploadOrders" >
                        <FileUpload />
                    </Route>
                    <Route path="/inputOrders">
                        <InputOrders />
                    </Route>
                    <Route path="/viewOrders">
                        <ViewOrders />
                    </Route>
                    <Route path="/viewToppings">
                        <ViewToppings />
                    </Route>
                    <Route path="/" />
                </Switch>
                <Footer></Footer>
            </Router>
        </OrderContext.Provider>  
        </>
    );
}

export default Home;
