import React, { Component, useRef, useState } from 'react';
import { Container, Button, Form, Row, Col, Card } from 'react-bootstrap';
import axios from 'axios';
import ViewOrderFormat from './viewOrderFormat';

function FileUpload() {

    const fileRef = useRef(null);
    const [File, setFile] = useState();
    const [Order, setOrder] = useState({});
    // const [Selected, setSelected] = useState(false);

    // const changeHandler = (event) => {
    //     setFile(event.target.files[0]);
    //     setSelected(true);
    // }

    const handleSubmit = () => {

        const formData = new FormData();
        formData.append('file', fileRef.current.files[0]);

        axios.post('/api/upload', formData, {
            headers: {'Content-Type':'multipart/form-data'}
        }).then(res => {
            if(res.data.error)
                console.log("ERROR SAVING");
            else
                setOrder(res.data.orderData.order);
        });

    }

    const toppingsArea = (area) => {
        if(area == "0")
            return "Whole";
        else if(area == "1")
            return "First-Half";
        else if(area == "2")
            return "Second-Half";
    }

    return (
        <Container>
        <Row>
            <Col xs={6} md={4}>
                <Form.Group controlId="formFile" className="mb-3" style={{paddingTop: "1rem"}}>
                    <Form.Label>Default file input example</Form.Label>
                    <Form.Control type="file" ref={fileRef}/>
                </Form.Group>
                <div>
                    <Button variant="primary" onClick={() => handleSubmit()}>Submit</Button>
                </div>
            </Col>
            <Col xs={12} md={8}>
                <Container>
                    <Card style={{marginTop: "1rem"}}>
                    <Card.Header>Featured</Card.Header>
                        { Order.id ? (
                                <Card.Body style={{maxHeight:"400px", overflowY: "scroll"}}>
                                    <div>Order {Order.id.number}:</div>
                                    <ViewOrderFormat Order={Order} />
                                </Card.Body>
                        ) : (
                            <div></div>
                        )}
                    </Card>
                </Container>
            </Col>
        </Row>          
        </Container>
    )
}

export default FileUpload;
