import React, { Component, useRef, useState } from 'react';
import axios from 'axios';

function FileUpload() {

    const fileRef = useRef(null);
    const [File, setFile] = useState();
    const [Order, setOrder] = useState({});
    // const [Selected, setSelected] = useState(false);

    // const changeHandler = (event) => {
    //     setFile(event.target.files[0]);
    //     setSelected(true);
    // }

    const handleSubmit = () => {

        const formData = new FormData();
        formData.append('file', fileRef.current.files[0]);

        axios.post('/pml/upload', formData, {
            headers: {'Content-Type':'multipart/form-data'}
        }).then(res => {
            setOrder(res.data.orderData.order);
            console.log(res.data)
        });

    }

    return (
        <>
        <input type="file" name="file" ref={fileRef}></input>
        <div>
            <button onClick={() => handleSubmit()}>Submit</button>
        </div>
        { Order.id ? (
            <div>
                <div>Order {Order.id.number}:</div>
                { Order.pizza.map(pizza => {
                    return <div key={pizza.id.number}> Pizza {((pizza.id.number)+1)} - {pizza.size[0]}, {pizza.crust[0]}, {pizza.type[0]}</div>;
                }) }
            </div>
        ) : (
            <div></div>
        )}
                    
        </>
    )
}

export default FileUpload;
