import React, { Component, useState, useEffect } from 'react';
import FileUpload from './fileUpload';
import { BrowserRouter as Router, Route, Switch } from 'react-router-dom';
import Footer from './footer';
import Navi from './navi';
import ViewOrders from './viewOrders';
import ViewToppings from './viewToppings';
import axios from 'axios';
import OrderContext from './OrderContext';

function Home () {

    // const [totalPage, setTotalPage] = useState(1);
    // const [limit, setLimit] = useState(4);

    // const getTotalOrder = async () => {
    //     let uri = `/api/orders/total`;
    //     await axios.get(uri).then(res => {
    //         console.log('set Total page', Math.ceil(res.data.total/limit))
    //         setTotalPage(Math.ceil(res.data.total/limit));
    //     });
    // }

    // useEffect( async () => {
    //     await getTotalOrder();
    // });

    return (
        <>
        
            <Router>
                <Navi></Navi>
                <Switch>
                    <Route path="/uploadOrders" >
                        <FileUpload />
                    </Route>
                    <Route path="/viewOrders">
                        <ViewOrders />
                    </Route>
                    <Route path="/viewToppings">
                        <ViewToppings />
                    </Route>
                    <Route path="/" />
                </Switch>
                <Footer></Footer>
            </Router>  
        </>
    );
}

export default Home;
