import React, {useEffect, useState} from 'react';
import axios from 'axios';

import KairosPagination from './kairosPagination';

function ViewOrders () {

    const [order, setOrder] = useState([]);
    const [curPage, setCurPage] = useState(1);
    const [totalPage, setTotalPage] = useState(2);
    const limit = 10;

    const getOrder = async (pageNumber) => {
        let uri = `/api/orders`;
        if(pageNumber){
            uri = `/api/orders?limit=${limit}&page=${pageNumber}`;
        }
        await axios.get(uri).then(res => {
            setOrder(res.data);
        });
    }

    const getTotalOrder = async () => {
        let uri = `/api/orders/total`;
        await axios.get(uri).then(res => {
            console.log(Math.ceil(res.data.total/limit))
            setTotalPage(Math.ceil(res.data.total/limit));
        });
    } 

    const pageClickHandler = (pageNumber) => {
        console.log(totalPage);
        setCurPage(pageNumber);
        getOrder(pageNumber);
    };

    useEffect(() => {
        getTotalOrder();
        getOrder(curPage);
    }, []);

    return (
        <KairosPagination
            totPages={totalPage}
            currentPage={curPage}
            pageClicked={(ele) => {
                pageClickHandler(ele);
            }}>
                <Table striped bordered hover>
                    <thead>
                        <tr>
                            <th>#</th>
                            <th>Order Number</th>
                            <th>Order Date</th>
                            <th>Order Data</th>
                        </tr>
                    </thead>
                    <tbody>
                        {order.length > 0 ? (
                            <tr>
                                {order.map(o => {
                                    return (
                                        <li > ORDER NO. {o.orderData.order.id.number} </li>)
                                })}
                            </tr>
                        ) : (
                        <tr>
                            <td colSpan="4">No Order Found</td>
                        </tr>)}
                    </tbody>
                </Table>
        </KairosPagination>
    );
}

export default ViewOrders;
