        var currentPage = parseInt(props.currentPage);
        var pageArr = [];
        if (totPages > 1) {
            if (totPages <= 9) {
                var i = 1;
                while (i <= totPages) {
                    pageArr.push(i);
                    i++;
                }
            } else {
                if (currentPage <= 5) 
                    pageArr = [1, 2, 3, 4, 5, 6, 7, 8, "", totPages];
                else if (totPages - currentPage <= 4)
                    pageArr = [
                        1,
                        "",
                        totPages - 7,
                        totPages - 6,
                        totPages - 5,
                        totPages - 4,
                        totPages - 3,
                        totPages - 2,
                        totPages - 1,
                        totPages
                    ];
                else
                    pageArr = [
                        1,
                        "",
                        currentPage - 3,
                        currentPage - 2,
                        currentPage - 1,
                        currentPage,
                        currentPage + 1,
                        currentPage + 2,
                        currentPage + 3,
                        "",
                        totPages
                    ];
            }
        }
        console.log('props.totPages- totPages', totPages);
        setPageArray(pageArr);

    return (
        <Pagination style={{ justifyContent: "center" }}>
            {Pagi()}
        </Pagination>
    )
}

export default KairosPagi;
