import React, {useEffect, useState, useContext, useRef} from 'react';
import axios from 'axios';
import { Table, Container, InputGroup, FormControl, Button } from 'react-bootstrap';

import KairosPagination from './kairosPagination';
import KairosPagi from './kairosPagi';
import OrderContext from './OrderContext';
import ViewOrderFormat from './viewOrderFormat';

function ViewOrders () {

    // const {totalPage, limit} = useContext(OrderContext);
    //console.log('viewOrders', totalPage);
    const [order, setOrder] = useState([]);
    const [curPage, setCurPage] = useState(1);
    const [totalPage, setTotalPage] = useState(1);
    const [limit, setLimit] = useState(4);
    const searchRef = useRef(null);

    const getOrder = async (pageNumber) => {
        let uri = `/api/orders`;
        if(pageNumber){
            uri = `/api/orders?limit=${limit}&page=${pageNumber}`;
        }
        await axios.get(uri).then(res => {
            setOrder(res.data);
        });
    }

    const getTotalOrder = async () => {
        let uri = `/api/orders/total`;
        await axios.get(uri).then(res => {
            console.log('set Total page', Math.ceil(res.data.total/limit))
            setTotalPage(Math.ceil(res.data.total/limit));
        });
    }

    const searchHandler = () => {
        console.log(searchRef.current.value);
    }

    const pageClickHandler = (pageNumber) => {
        // console.log('pageNumber', pageNumber)
         setCurPage(pageNumber);
         getOrder(pageNumber);
    };

    useEffect(async () => {
        await getTotalOrder();
        getOrder(curPage);
        
    }, []);

    return (
        <KairosPagi
            totalPage={totalPage}
            currentPage={curPage}
            pageClicked={(i) => {
                pageClickHandler(i);
            }}
            >
                <Container>
                <InputGroup className="mb-3" style={{paddingTop: "1rem"}}>
                    <FormControl
                        placeholder="Type Here"
                        aria-describedby="basic-addon2"
                        ref={searchRef}
                    />
                    <Button variant="outline-secondary" id="button-addon2" onClick={searchHandler}>
                        Search
                    </Button>
                </InputGroup>
                <Table striped bordered hover>
                    <thead>
                        <tr>
                            <th>#</th>
                            <th>Order Number</th>
                            <th>Order Date</th>
                            <th>Order Data</th>
                        </tr>
                    </thead>
                    {order.length > 0 ? (
                        <tbody>
                            {order.map((o, i) => {
                                return (<tr key={(i+1)}>
                                    <td > {(i+1)} </td>
                                    <td > {o.orderData.order.id.number} </td>
                                    <td > {o.orderDate} </td>
                                    <td > 
                                        <div>
                                            <ViewOrderFormat Order={o.orderData.order} />
                                        </div>
                                    </td>
                                </tr>)
                            })}
                        </tbody>
                    ) : (
                        <tbody>    
                            <tr>
                                <td colSpan="4">No Order Found</td>
                            </tr>
                        </tbody>)
                    }
                </Table>
            </Container>
        </KairosPagi>
    );

    return (
        <div sample={totalPage}>
        <KairosPagination
            totPages={totalPage}
            currentPage={curPage}
            pageClicked={(ele) => {
                pageClickHandler(ele);
            }}>
            <Container>
                <InputGroup className="mb-3" style={{paddingTop: "1rem"}}>
                    <FormControl
                        placeholder="Type Here"
                        aria-describedby="basic-addon2"
                        ref={searchRef}
                    />
                    <Button variant="outline-secondary" id="button-addon2" onClick={searchHandler}>
                        Search
                    </Button>
                </InputGroup>
                <Table striped bordered hover>
                    <thead>
                        <tr>
                            <th>#</th>
                            <th>Order Number</th>
                            <th>Order Date</th>
                            <th>Order Data</th>
                        </tr>
                    </thead>
                    {order.length > 0 ? (
                        <tbody>
                            {order.map((o, i) => {
                                return (<tr key={(i+1)}>
                                    <td > {(i+1)} </td>
                                    <td > {o.orderData.order.id.number} </td>
                                    <td > {o.orderDate} </td>
                                    <td > 
                                        <ViewOrderFormat Order={o.orderData.order} />
                                    </td>
                                </tr>)
                            })}
                        </tbody>
                    ) : (
                        <tbody>    
                            <tr>
                                <td colSpan="4">No Order Found</td>
                            </tr>
                        </tbody>)
                    }
                </Table>
            </Container>
        </KairosPagination>
        </div>
    );
}

export default ViewOrders;
